INSERT IGNORE INTO `forms` (`module`, `internalName`, `name`, `description`, `dataset`, `groupe`, `formMethod`, `formAction`, `cat`, `type`, `yamlStructure`, `options`, `printModel`, `cda`, `javascript`) VALUES
('gynobs', 'gynObsATCD', 'Formulaire latéral écran patient principal (atcd)', 'formulaire en colonne latéral du dossier patient (atcd)', 'data_types', 'medical', 'post', NULL, @catID, 'public', '---\nstructure:\n  row1:\n    col1:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - poids,plusg={<i class=\"fas fa-clone duplicate\"></i>},plus={kg},class=text-right\n    col2:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - taillePatient,plusg={<i class=\"fas fa-clone duplicate\"></i>},plus={cm},class=text-right\n    col3:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - imc,readonly,plus={kg/m²},class=text-right\n  row2:\n    col1:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - groupeSanguin\n    col2:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - toxoStatut\n    col3:\n      size: 12 col-12 col-sm-4 col-lg-4\n      bloc:\n      - rubStatut\n  row3:\n    col1:\n      size: col-5\n      bloc:\n      - clairanceCreatinine,plus={mL/min},class=text-right\n    col2:\n      size: col-7\n      bloc:\n      - insuffisanceHepatique\n  row4:\n    col1:\n      size: 12\n      bloc:\n      - job,rows=1\n      - allergies,rows=1\n      - toxiques,rows=1\n  row5:\n    col1:\n      size: 12\n      bloc:\n      - atcdObs,rows=2\n      - atcdPersoGyneco,rows=2\n      - atcdMedicChir,rows=2\n      - atcdFamiliaux,rows=2\n...\n', NULL, NULL, NULL, '$(document).ready(function() {\r\n\r\n  //calcul IMC\r\n  if ($(\'#id_imc_id\').length > 0) {\r\n\r\n    imc = imcCalc($(\'#id_poids_id\').val(), $(\'#id_taillePatient_id\').val());\r\n    if (imc > 0) {\r\n      $(\'#id_imc_id\').val(imc);\r\n    }\r\n\r\n    $(\"#patientLatCol\").on(\"keyup\", \"#id_poids_id , #id_taillePatient_id\", function() {\r\n      poids = $(\'#id_poids_id\').val();\r\n      taille = $(\'#id_taillePatient_id\').val();\r\n      imc = imcCalc(poids, taille);\r\n      $(\'#id_imc_id\').val(imc);\r\n      patientID = $(\'#identitePatient\').attr(\"data-patientID\");\r\n      setPeopleDataByTypeName(imc, patientID, \'imc\', \'#id_imc_id\', \'0\');\r\n\r\n    });\r\n  }\r\n  \r\n  //ajutement auto des textarea en hauteur\r\n  autosize($(\'#formName_gynObsATCD textarea\'));\r\n\r\n});');
