('gynobs', 'gynObsEcho12', 'Formulaire Echo 12', 'formulaire écho 12', 'data_types', 'medical', 'post', '/patient/ajax/saveCsForm/', @catID, 'public', '---\nglobal:\n  formClass: newCS\nstructure:\n  row1:\n    head: Echographie 1er trimestre\n    col1:\n      size: 4\n      bloc:\n      - indicEcho12\n    col2:\n      size: 4\n      bloc:\n      - voieExamEcho12\n    col3:\n      size: 4\n      bloc:\n      - conditionsEcho12\n  row2:\n    col1:\n      size: 4\n      bloc:\n      - nbFoetusEcho12,min=1,max=3,step=1\n    col2:\n      size: 4\n      bloc:\n      - e12typeGro,disabled\n    col3:\n      size: 4\n      bloc:\n      - e12membrane,disabled\n  row3:\n    class: foetusA\n    head: Foetus A\n    col1:\n      size: 3\n      bloc:\n      - actiCardioFA\n      - bipFA,plus={mm}\n      - e12femurA,plus={mm}\n    col2:\n      size: 3\n      bloc:\n      - rcfFA,plus={bpm}\n      - paFA,plus={mm}\n    col3:\n      size: 3\n      bloc:\n      - mafFA\n      - cnFA,plus={mm}\n    col4:\n      size: 3\n      bloc:\n      - lccA,plus={mm}\n      - termeLCCFA,readonly,plus={<i class=\"fas fa-save\"></i>}\n  row4:\n    class: foetusA\n    col1:\n      size: 4\n      bloc:\n      - morphoCephaFA,rows=4\n    col2:\n      size: 4\n      bloc:\n      - abdoFA,rows=4\n    col3:\n      size: 4\n      bloc:\n      - membresFA,rows=4\n  row5:\n    class: foetusA\n    col1:\n      size: 3\n      bloc:\n      - E12liquideA\n    col2:\n      size: 3\n      bloc:\n      - e12trophoFA\n    col3:\n      size: 3\n      bloc:\n      - e12trophoAspectFA\n    col4:\n      size: 3\n      bloc:\n      - e12decoFA\n  row6:\n    class: foetusB\n    head: Foetus B\n    col1:\n      size: 3\n      bloc:\n      - actiCardioFB,disabled\n      - bipFB,disabled,plus={mm}\n      - e12femurB,disabled,plus={mm}\n    col2:\n      size: 3\n      bloc:\n      - rcfFB,disabled,plus={bpm}\n      - paFB,disabled,plus={mm}\n    col3:\n      size: 3\n      bloc:\n      - mafFB,disabled\n      - cnFB,disabled